#include <iostream>
#include <string>
#include <cassert>
#include <vector>
#include <BeeNum/Bint.h>
#include <BeeNum/Math.h>

using namespace BeeNum;


int main() {
	Bint a;
	Bint c;

	std::vector<std::vector<std::string>> data = {
		{"10", "55"},
		{"11", "89"},
		{"399", "108788617463475645289761992289049744844995705477812699099751202749393926359816304226"},
		{"400", "176023680645013966468226945392411250770384383304492191886725992896575345044216019675"},
		{"401", "284812298108489611757988937681460995615380088782304890986477195645969271404032323901"},
		{"0", "0"},
		{"1", "1"},
		{"2", "1"},
		{"10000", ""},
		{"10001", ""},
	};

	///////////////////////////////////////////////////////////

	std::cout << "Fibonnaci:" << std::endl;

	std::cout << "positives:" << std::endl;
	for (auto& s : data) {
		a = s[0];
		std::cout << "a = " << a << std::endl;
		std::cout << "a = " << a.bin() << std::endl;
		c = Math::fib(std::stoi(a));
		std::cout << "c = " << c << std::endl;
		std::cout << "s = " << s[1] << std::endl;
		assert(c.toString()==s[1]);
		std::cout << std::endl;
	}

}
